import React from "react";
import "./Card.css";

interface Props {
  name: string;
  ingredients: string;
  image?: any;
}

const Card = (props: Props) => {
  return (
    <div className="cardContainer">
      <div className="cardImageContainer">
        <img src={props.image}></img>
      </div>
      <h3>{props.name}</h3>
      <p>{props.ingredients}</p>
      <button>ADD TO CART</button>
    </div>
  );
};

export default Card;
