import React, { useState } from "react";
import "./SizeModal.css";

//import Symbols
import CloseIcon from "@mui/icons-material/Close";
import { PizzaSize } from "../../helpers/enums/PizzaSize";

export interface Props {
  setOpenModal: (state: boolean) => void;
  price: number;
}

const SizeModal = (props: Props) => {
  const [small, setSmall] = useState<boolean>(false);
  const [medium, setMedium] = useState<boolean>(false);
  const [large, setLarge] = useState<boolean>(false);
  const [xlarge, setXLarge] = useState<boolean>(false);

  const handleSelection = (size: any) => {
    switch (size) {
      case "small":
        setSmall(!small);
        setMedium(false);
        setLarge(false);
        setXLarge(false);
      case "medium":
        setSmall(!medium);
        setSmall(false);
        setLarge(false);
        setXLarge(false);
      case "large":
        setSmall(!large);
        setMedium(false);
        setSmall(false);
        setXLarge(false);
      case "xlarge":
        setSmall(!xlarge);
        setMedium(false);
        setLarge(false);
        setSmall(false);
    }
  };

  const addCart = () => {
    props.setOpenModal(false);
  };
  return (
    <div className="sizeContainer">
      <div className="selectSizeContainer">
        <div className="sizeCloseButton">
          <CloseIcon
            className="sizeCloseIcon"
            onClick={() => {
              props.setOpenModal(false);
            }}
          />
        </div>
        <div className="sizeContentContainer">
          <h3 className="sizeTitle">Select the size of your pizza below:</h3>
          <button className="sizeSelection" onClick={handleSelection("small")}>
            Small: ${props.price - 2}
          </button>
          <button className="sizeSelection">Medium: ${props.price}</button>
          <button className="sizeSelection">Large: ${props.price + 3}</button>
          <button className="sizeSelection">
            Extra Large: ${props.price + 4.5}
          </button>
        </div>
        <div className="sizeButtonContainer">
          <button className="sizeButton" onClick={addCart}>
            ADD TO CART
          </button>
        </div>
      </div>
      <div className="sizeModalBlur"></div>
    </div>
  );
};

export default SizeModal;
