import "./UpdateModal.css";
import { useState } from "react";
//import Symbols
import CloseIcon from "@mui/icons-material/Close";
import DeleteIcon from "@mui/icons-material/Delete";
import {
  IIngredients,
  IOrder,
  IOrderPizza,
} from "../../helpers/interfaces/IOrder";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import Axios from "axios";

export interface Props {
  setUpdateModal: (state: boolean) => void;
  pizza: IOrderPizza;
  order: IOrder;
}
{
}

const UpdateModal = (props: Props) => {
  const [changes, setChanges] = useState<IOrder>(props.order);
  const client = useQueryClient();
  const { mutate: mutateUser } = useMutation(
    (): Promise<any> => {
      return Axios.put(
        "http://localhost:3001/orders/updateOrder",
        changes
      ).then((res) => res.data);
    },
    {
      onMutate: async (updates) => {
        // // Cancel any outgoing refetches (so they don't overwrite our optimistic update)
        // await client.cancelQueries(["order", userUpdates.id]);

        // // Snapshot the previous value
        // const previousUser = client.getQueryData(["user", userUpdates.id]);

        // Optimistically update to the new value
        client.setQueryData(["user", updates._id], userUpdates);

        // Return a context with the previous user and updated user
        return { previousUser, userUpdates }; // context
      },
      // If the mutation fails, use the context we returned above
      onError: (err, userUpdates, context) => {
        client.setQueryData(
          ["user", context.userUpdates.id],
          context.previousUser
        );
      },
      // Always refetch after error or success:
      onSettled: (userUpdates) => {
        client.invalidateQueries(["user", userUpdates.id]);
      },
    }
  );

  const increment = (ingredient: IIngredients) => {
    setChanges((prev) => ({
      ...prev,
      items: [
        ...props.order.items,
        {
          ...props.pizza,
          ingredients: [
            ...props.pizza.ingredients,
            { ...ingredient, count: ingredient.count + 1 },
          ],
          price: props.pizza.price + 1,
        },
      ],
    }));
  };

  const decrement = (ingredient: IIngredients) => {
    setChanges((prev) => ({
      ...prev,
      items: [
        ...props.order.items,
        {
          ...props.pizza,
          ingredients: [
            ...props.pizza.ingredients,
            { ...ingredient, count: ingredient.count - 1 },
          ],
          price: props.pizza.price - 1,
        },
      ],
    }));
  };

  const saveChanges = () => {};

  return (
    <div className="updateContainer">
      <div className="updateInfoContainer">
        <div className="updateCloseButton">
          <CloseIcon
            className="updateCloseIcon"
            onClick={() => {
              props.setUpdateModal(false);
            }}
          />
        </div>
        <div className="updateInfo">
          <div className="updateInfoHeader">
            <div className="updatePizzaName">Pesto Chicken Pizza</div>
            <DeleteIcon className="updateDeleteIcon" />
          </div>
          <div className="updateInfoIngredients">
            {props.pizza.ingredients.map((ingredient: IIngredients) => (
              <div className="updateIngredients">
                <p className="ingredientName"> {ingredient.ingredient} </p>
                <button
                  className="ingredientButton"
                  onClick={() => decrement(ingredient)}
                >
                  -
                </button>
                <input
                  className="ingredientCountValue"
                  defaultValue={ingredient.count}
                />
                <button
                  className="ingredientButton"
                  onClick={() => increment(ingredient)}
                >
                  {" "}
                  +
                </button>
              </div>
            ))}
          </div>
          <div className="updateInfoSize">
            <button className="updateSizeSelection">Small</button>
            <button className="updateSizeSelection">Medium</button>
            <button className="updateSizeSelection">Large</button>
            <button className="updateSizeSelection">Extra Large</button>
          </div>
          <div className="updateInfoPrice">
            <p className="updatedPrice">Updated price: $17</p>
          </div>
        </div>
        <div className="updateSubmitButton">
          <button className="updateModalButton">UPDATE</button>
        </div>
      </div>
      <div className="updateModalBlur"></div>
    </div>
  );
};

export default UpdateModal;
