import { useState } from "react";
import "./Order.css";

import OrderPizza from "../../helpers/interfaces/IOrder";

export interface Props {
  date: string;
  completed: boolean;
  items: [OrderPizza];
}

const Order = (props: Props) => {
  const [completed, setCompleted] = useState<boolean>(false);
  return (
    <div className="orderContainer">
      <div className="orderHeader">
        <div className="orderDate">ORDER DATE: 29/07/2022</div>
        <div className="orderCompletion">
          {completed ? (
            <div className="orderCompleted">COMPLETED</div>
          ) : (
            <div className="orderIncompleted">INCOMPLETED</div>
          )}
        </div>
      </div>
      <div className="orderItems">
        <div className="orderInfo">
          <div className="orderPizzaName">Pepperoni</div>
          <div className="orderPizzaIngredients">
            Tomato Sauce, Mozzarella, Fior di Latte, Grana Padano, Prosciutto di
            Parma, Arugula
          </div>
          <div className="orderBottom">
            <div className="orderPizzaSize">Size: Medium</div>
            <div className="orderPizzaPrice">Price: $15</div>
          </div>
        </div>
        <div className="orderButton">
          <button className="updateButton">Change Pizza</button>
        </div>
      </div>
    </div>
  );
};

export default Order;
