import React from "react";
import "./Menu.css";
import { pizzas } from "../../storage/pizzaMenu";
import { Pizza } from "../../helpers/interfaces/IPizza";

//Import Components
import Navbar from "../../components/Navbar/Navbar";
import Card from "../../components/Card/Card";
import Footer from "../../components/Footer/Footer";
import { IIngredients } from "../../helpers/interfaces/IOrder";

const Menu = () => {
  return (
    <>
      <Navbar />
      <div className="menuCardContainer">
        {pizzas.map((pizza: Pizza, key: number) => {
          let ingredients = pizza.ingredients.map((element: IIngredients) => {
            return element.ingredient;
          });
          return (
            <Card
              key={key}
              name={pizza.name}
              ingredients={ingredients.join(", ")}
              image={pizza.image}
              price={pizza.price}
            />
          );
        })}
      </div>
      <Footer />
    </>
  );
};

export default Menu;
