import { useState } from "react";
import "./Order.css";

export interface Props {
  date: string;
  completed: boolean;
  items: any;
}

const Order = (props: Props) => {
  return (
    <div className="orderContainer">
      <div className="orderHeader">
        <div className="orderDate">ORDER DATE: {props.date}</div>
        <div className="orderCompletion">
          {props.completed ? (
            <div className="orderCompleted">COMPLETED</div>
          ) : (
            <div className="orderIncompleted">INCOMPLETED</div>
          )}
        </div>
      </div>
      <div className="orderItems">
        <div className="orderInfo">
          <div className="orderPizzaName">{props.items.name}</div>
          <div className="orderPizzaIngredients">{props.items.ingredients}</div>
          <div className="orderBottom">
            <div className="orderPizzaSize">Size: {props.items.size}</div>
            <div className="orderPizzaPrice">Price: ${props.items.price}</div>
          </div>
        </div>
        <div className="orderButton">
          <button className="updateButton">Change Pizza</button>
        </div>
      </div>
    </div>
  );
};

export default Order;
